import React from "react";


class PdfParse extends React.Component {

    constructor() {
      super();
      this.state = {
         text: "",
      }
      // You only need this if you don't use an arrow function when creating a function
      // this.submitPDF = this.submitPDF.bind(this);
   }
   submitPDF = () => {
      if (this.state.text === ""){
      this.setState({text: "Pdf Text Will Display Here"});
      } else {
         this.setState({text:""});
      }
   }

   render() {
      return (
         <div>
            <header className="App">
            <h1>BidScope.IO</h1>
            </header>
            <div>
               <h2 className="App-content">Upload Your Plans</h2>
               <button onClick={this.submitPDF}>Click to Upload</button>
               <p onClick={this.submitPDF}>{this.state.text}</p>
            </div>
          </div>
      );
   }
}



export default PdfParse;
