import React from "react";


class PdfParse extends React.Component {

    constructor() {
      super();
      this.state = {
         text: "",
      }
      // You only need this if you don't use an arrow function when creating a function
      // this.submitPDF = this.submitPDF.bind(this);
   }
   submitPDF = () => {
      if (this.state.text === ""){
      this.setState({text: "Pdf Text Will Display Here"});
      } else {
         this.setState({text:""});
      }
   }

   render() {
      return (
         <div>
            <div className="App-content">
               <h2>Upload Your Plans</h2>
                  <div className="form">
                     <form className="pdf-form">
                        <input type="text" name="file"></input>
                     </form>
                     <div>
                        <button className="form-button" onClick={this.submitPDF}>Click to Upload</button>
                     </div>
                  </div>
               <p onClick={this.submitPDF}>{this.state.text}</p>
            </div>
          </div>
      );
   }
}



export default PdfParse;
