import React from "react";


class PdfParse extends React.Component {

    constructor() {
      super();
      this.state = {
         text: "",
      }
      // You only need this if you don't use an arrow function when creating a function
      // this.submitPDF = this.submitPDF.bind(this);
   }
   submitPDF = () => {
   // If absolute URL from the remote server is provided, configure the CORS
   // header on that server.
      var url = () => document.getElementById('file').value.console.log(value)
      // 'https://raw.githubusercontent.com/mozilla/pdf.js/ba2edeae/examples/learning/helloworld.pdf';
      // Loaded via <script> tag, create shortcut to access PDF.js exports.
      var pdfjsLib = window['pdfjs-dist/build/pdf'];

      // The workerSrc property shall be specified.
      pdfjsLib.GlobalWorkerOptions.workerSrc = '//mozilla.github.io/pdf.js/build/pdf.worker.js';

      // Asynchronous download of PDF
      var loadingTask = pdfjsLib.getDocument(url);
      loadingTask.promise.then(function(pdf) {
      console.log('PDF loaded');

      // Fetch the first page
      var pageNumber = 1;
      pdf.getPage(pageNumber).then(function(page) {
         console.log('Page loaded');

         var scale = 1.5;
         var viewport = page.getViewport({scale: scale});

         // Prepare canvas using PDF page dimensions
         var canvas = document.getElementById('the-canvas');
         var context = canvas.getContext('2d');
         canvas.height = viewport.height;
         canvas.width = viewport.width;

         // Render PDF page into canvas context
         var renderContext = {
            canvasContext: context,
            viewport: viewport
         };
         var renderTask = page.render(renderContext);
         renderTask.promise.then(function () {
            console.log('Page rendered');
         });
      });
      }, function (reason) {
      // PDF loading error
      console.error(reason);
      });
   }

   render() {
      return (
         <div>
            <section className="App-content">
               <div>
                  <h2>Upload Your Plans</h2>
                     {/* <form className="form pdf-form">
                        <input type="text"></input>
                     </form> */}
                  <div className="form">
                  <input type="file" id="file"/>
                     <button type="file" name="file" className="form-button" onClick={this.submitPDF}>Scan PDF</button>
                  </div>
               </div>
            </section>
            <section className="App section">
               <p onClick={this.submitPDF}>{this.state.text}</p>

<h1>PDF.js 'Hello, world!' example</h1>

<canvas id="the-canvas"></canvas>
            </section>
          </div>
      );
   }
}



export default PdfParse;
